mapper.update:
update etljob 
set status = #{status}
	,recently_run_date=#{recently_run_date}
	,log = #{log}		
 where mappingid = #{mappingid}

mapper.select:
select * from etljob
where jobtype = #{jobtype}
order by create_date desc
limit ${offset},${size}

select * from etljob
where jobtype = #{jobtype}
order by recently_run_date desc

mapper.delete:
delete from etljob
where id = #{id}
and jobtype = {type}

delete from etljob where jobtype = {type}
and id in

    #{ETLJobItem}
